$(document).ready(function () {
    $(window).scroll(function () {
        var height = $(window).scrollTop();
        var topNav=$('.top-nav');
        var logo=$('.header-logo');
        var mainNav=$('.main-nav');
        if (height > 150) {
            topNav.addClass('top-nav-hidden');
            logo.addClass('header-logo-smaller');
            mainNav.addClass('main-nav-hidden');
        }else{
            topNav.removeClass('top-nav-hidden');
            logo.removeClass('header-logo-smaller');
            mainNav.removeClass('main-nav-hidden');
        }
    });
    var menu = $(".menu-container");
    menu.show();
    var menuWidth = menu.width();
    var body = $(".body-container");
    menu.css('left', '-' + menuWidth + 'px');
    $(".menu-ord").click(function () {
        $(this).addClass('menu-ord-click');
        body.addClass('body-container-move');
        body.css('margin-left', (menuWidth + 15) + 'px');
        menu.css('left', '0px');

    });
    $(".body-shdow").click(function () {
        $(".menu-ord").removeClass('menu-ord-click');
        body.removeClass('body-container-move');
        body.css('margin-left', '0px');
        menu.css('left', '-' + menuWidth + 'px');

    });
});


